import { Component, OnInit } from '@angular/core';
import { Geolocation } from '@ionic-native/geolocation/ngx';
import { RestaurantService } from '../services/restaurant.service';


@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage implements OnInit {

  restaurants: any[] = [];
  limit: number = 10;
  skip: number = 0;
  latitude: number | undefined;
  longitude: number | undefined;

  constructor(private geolocation: Geolocation, private restaurantService: RestaurantService) {}

  ngOnInit() {
    this.geolocation.getCurrentPosition().then((resp) => {
      this.latitude = resp.coords.latitude;
      this.longitude = resp.coords.longitude;
      this.loadRestaurants();
    }).catch((error) => {
      console.log('Error getting location', error);
    });
  }

  loadRestaurants(event?: any) {
    if (this.latitude !== undefined && this.longitude !== undefined) {
      this.restaurantService.getRestaurants(this.latitude, this.longitude, this.limit, this.skip).subscribe((data) => {
        this.restaurants = this.restaurants.concat(data.response); // API'den dönen veriye uygun şekilde ayarlayın
        console.log(data,this.restaurants)
        if (event) {
          event.target.complete();
        }
        this.skip += this.limit;
      });
    }
  }

  loadMore(event:any) {
    this.loadRestaurants(event);
  }

}
<ion-header [translucent]="true">
  <ion-toolbar style="background-color: white;">
    <ion-buttons slot="start">
      <ion-button>
        <img src="assets/icon/menu.png">
      </ion-button>
    </ion-buttons>
    <ion-buttons slot="end">
      <ion-button>
        <img src="assets/icon/shopping.png">
      </ion-button>
      <ion-button>
        <img src="assets/icon/pastOrders.png">
      </ion-button>
    </ion-buttons>
    <ion-title>Ana Sayfa</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content style="font-family: Montserrat; font-size: 18px;" class="ion-padding">
  <ion-searchbar placeholder="Mekan ara"></ion-searchbar>

  <h2><b>Sonuçlar</b></h2>

  <ion-list>
    <ion-item *ngFor="let restaurant of restaurants">
      <ion-card>
        <ng-container *ngIf="restaurant.images[0]?.base64">
          <img style="height: 150px; width: 343px;" [src]="restaurant.images[0].base64" />
        </ng-container>
        <ion-card-content>
          <div class="rating">
            <ion-badge color="danger">{{ restaurant.rate }}</ion-badge>
          </div>
          <ion-card-title>{{ restaurant.title }}</ion-card-title>
          <p>{{ restaurant.text }}</p>
          <p><ion-icon name="location-outline"></ion-icon> {{ restaurant.distance }} km yakınında</p>
          <p><ion-icon name="basket"></ion-icon> Min. Sipariş Tutarı: ₺{{ restaurant.storeInfo.minOrderPrice }}</p>
          <p>
            <ion-icon name="time-outline"></ion-icon>
            <span [ngClass]="{'open': restaurant.storeInfo.status === 'open', 'closed': restaurant.storeInfo.status === 'closed'}">
              {{ restaurant.storeInfo.status === 'open' ? 'İşletme Açık' : 'İşletme Kapalı' }}
            </span>
            {{ restaurant.storeInfo.workingHours[0].close }} / {{ restaurant.storeInfo.workingHours[0].close }}
          </p>

        </ion-card-content>
      </ion-card>
    </ion-item>
  </ion-list>

  <ion-infinite-scroll threshold="100px" (ionInfinite)="loadMore($event)">
    <ion-infinite-scroll-content loadingSpinner="bubbles" loadingText="Loading more data...">
    </ion-infinite-scroll-content>
  </ion-infinite-scroll>
</ion-content>
